questions.answer_code:
#include <stdio.h>

int main() {
    int n, isPrime = 1;
    scanf("%d", &n);
    if (n <= 1) isPrime = 0;
    for(int i=2; i*i <= n; i++) {
        if(n % i == 0) {
            isPrime = 0;
            break;
        }
    }
    if(isPrime) printf("Yes");
    else printf("No");
    return 0;
}

