    int n;
    scanf("%d", &n);
    // 计算第n项
    return 0;
}

questions.answer_code:
#include <stdio.h>

int main() {
    int n, t1 = 1, t2 = 1, nextTerm;
    scanf("%d", &n);
    if (n == 1 || n == 2) printf("1");
    else {
        for (int i = 3; i <= n; ++i) {
            nextTerm = t1 + t2;
            t1 = t2;
            t2 = nextTerm;
        }
        printf("%d", t2);
    }
    return 0;
}

